hudson.tasks.Shell.command:
#!/bin/bash
if [ ! -e opt ]; then mkdir opt fi
if [ ! -e opt/gcc-linaro-4.9-2015.02-3-x86_64_aarch64-linux-gnu.tar.xz ]; then cd opt wget -q -c https://releases.linaro.org/15.02/components/toolchain/binaries/aarch64-linux-gnu/gcc-linaro-4.9-2015.02-3-x86_64_aarch64-linux-gnu.tar.xz tar -Jxf gcc-linaro-4.9-2015.02-3-x86_64_aarch64-linux-gnu.tar.xz cd .. else if [ ! -e opt/gcc-linaro-4.9-2015.02-3-x86_64_aarch64-linux-gnu ]; then cd opt tar -Jxf gcc-linaro-4.9-2015.02-3-x86_64_aarch64-linux-gnu.tar.xz cd .. fi fi
cd ${WORKSPACE} cd local/estuary_test/boot-app-scripts : ${SHELL_PLATFORM:=`python parameter_parser.py -f config.yaml -s Build -k Platform`} : ${SHELL_DISTRO:=`python parameter_parser.py -f config.yaml -s Build -k Distro`}
: ${BOOT_PLAN:=`python parameter_parser.py -f config.yaml -s Jenkins -k Boot`} : ${APP_PLAN:=`python parameter_parser.py -f config.yaml -s Jenkins -k App`}
: ${USER:=`python parameter_parser.py -f config.yaml -s LAVA -k USER`} : ${HOST:=`python parameter_parser.py -f config.yaml -s LAVA -k HOST`} : ${LAVA_SERVER:=`python parameter_parser.py -f config.yaml -s LAVA -k lavaserver`} : ${LAVA_USER:=`python parameter_parser.py -f config.yaml -s LAVA -k lavauser`} : ${LAVA_STREAM:=`python parameter_parser.py -f config.yaml -s LAVA -k lavastream`} : ${LAVA_TOKEN:=`python parameter_parser.py -f config.yaml -s LAVA -k TOKEN`}
: ${KERNELCI_SERVER:=`python parameter_parser.py -f config.yaml -s Kernelci -k server`} : ${KERNELCI_TOKEN:=`python parameter_parser.py -f config.yaml -s Kernelci -k token`} : ${FTP_SERVER:=`python parameter_parser.py -f config.yaml -s Kernelci -k ftpserver`}
echo $ARCH_MAP : ${ARCH_MAP:=`python parameter_parser.py -f config.yaml -s Arch`} echo $ARCH_MAP
echo $GIT_DESCRIBE cat << EOF > ${WORKSPACE}/env.properties GIT_DESCRIBE=$GIT_DESCRIBE SHELL_PLATFORM=$SHELL_PLATFORM SHELL_DISTRO=$SHELL_DISTRO BOOT_PLAN=$BOOT_PLAN APP_PLAN=$APP_PLAN USER=$USER HOST=$HOST LAVA_SERVER=$LAVA_SERVER LAVA_USER=$LAVA_USER LAVA_STREAM=$LAVA_STREAM LAVA_TOKEN=$LAVA_TOKEN KERNELCI_SERVER=$KERNELCI_SERVER KERNELCI_TOKEN=$KERNELCI_TOKEN FTP_SERVER=$FTP_SERVER ARCH_MAP=$ARCH_MAP TFTP_DIR=$TFTP_DIR EOF
cat ${WORKSPACE}/env.properties
